CREATE TABLE IF NOT EXISTS "profile" ("userId" INTEGER PRIMARY KEY NOT NULL UNIQUE, "username" TEXT NOT NULL UNIQUE, "name" TEXT, "age" TEXT, "enlisted" TEXT, "lastSeen" TEXT,"presentation" TEXT, "country" TEXT, "veteranStatus" TEXT, "statusMessage" TEXT, "statusMessageDate" TEXT);
CREATE TABLE IF NOT EXISTS "personas" ("personaId" INTEGER PRIMARY KEY NOT NULL UNIQUE, "userId" INTEGER NOT NULL,"name" TEXT, "platform" TEXT, "image" text);
CREATE TABLE IF NOT EXISTS "platoon" ("platoonId" INTEGER PRIMARY KEY NOT NULL UNIQUE, "userId" INTEGER NOT NULL, "name" TEXT NOT NULL, "tag" TEXT, "platformId" INTEGER, "platform" TEXT DEFAULT "", "gameId" INTEGER DEFAULT 0, "dateCreated" INTEGER DEFAULT 0, "website" TEXT DEFAULT "", "badge" TEXT DEFAULT "", "presentation" TEXT DEFAULT "", "numberOfFans" INTEGER, "numberOfMembers" INTEGER, "blazeClubId" INTEGER DEFAULT 0,"membersCount" TEXT, "timestamp" INTEGER);
CREATE TABLE IF NOT EXISTS "rankProgress" ("_id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "personaId" INTEGER NOT NULL, "personaName" TEXT NOT NULL ,"platform" TEXT, "rank" INTEGER, "currentRankScore" INTEGER, "nextRankScore" INTEGER, "score" INTEGER);
CREATE TABLE IF NOT EXISTS "personaStatistics" ("_id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "personaId" INTEGER NOT NULL, "kills" TEXT, "killAssists" TEXT, "vehicleDestroyed" TEXT, "vehicleAssists" TEXT, "heals" TEXT, "revives" TEXT, "repairs" TEXT, "resupplies" TEXT, "deaths" TEXT, "kdRatio" TEXT, "wins" TEXT, "losses" TEXT, "wlRatio" TEXT, "accuracy" TEXT, "longestHeadshot" TEXT, "longestKillstreak" TEXT, "skillrating" TEXT, "timePlayed" TEXT, "scorePerMinute" TEXT);
CREATE TABLE IF NOT EXISTS "scoreStatistics" ("_id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "personaId" INTEGER NOT NULL, "assault" TEXT, "engineer" TEXT, "support" TEXT, "recon" TEXT, "jet" TEXT, "tank" TEXT, "ifv" TEXT, "antiAir" TEXT, "attackHeli" TEXT, "scoutHeli" TEXT, "combat" TEXT, "award" TEXT, "unlocks" TEXT, "totalScore" text);
